mapper.select:

select id, category, name, price
from ITEM
where category=#{category}


select id, member_ID, item_ID,wish
from MY_ITEM
where item_Id=#{itemId} and member_ID=#{memberId}

mapper.update:

update MY_ITEM
set wish = 0
where member_Id=#{memberId} and item_Id=#{itemId}


update MY_ROOM
set floor=#{itemId}
where member_Id=#{memberId}


update MY_ROOM
set wallpaper=#{itemId}
where member_Id=#{memberId}


update MY_ROOM
set bed=#{itemId}
where member_Id=#{memberId}


update MY_ROOM
set closet=#{itemId}
where member_Id=#{memberId}


update MY_ROOM
set desk=#{itemId}
where member_Id=#{memberId}


update MY_ROOM
set walldeco=#{itemId}
where member_Id=#{memberId}

mapper.select:

select member_id, floor, wallpaper, bed, closet,desk, walldeco
from MY_ROOM
where member_id=#{id}

mapper.update:

UPDATE MEMBER
SET POINT = POINT - #{price}
WHERE ID = #{memberId}
